import React from 'react'
import ThermostatTwoToneIcon from '@mui/icons-material/ThermostatTwoTone';
import Paper from '@mui/material/Paper';
import { useState , useEffect } from 'react';

const WeatherCard = () => {

    const [value , setValue] = useState();
    const [search , setSearch] = useState("Mumbai");
    const inputText = (e) =>
    {
        setSearch(e.target.value)
    };

    useEffect(()=>{

        const apiCall = async()=>
        {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=841d4725a816a6a06d403632bd166509`
            const res = await fetch(url);
            const result = await res.json();
            console.log(result)
            setValue(result.main);
        }

        apiCall();

    },[search]);

  return (
    <>
        <div className='card__div'>

        <Paper sx={{
            width : "25%" ,
            height : "60vh" ,
            borderRadius : "5px" , 
            padding : "20px" , 
            backgroundColor : "#A0B7DC"
            }} elevation={6} 
            className='paper'
            > 
            

            <div className='card__items'>
                <input type="search" value={search} onChange={inputText} />
                <h1><ThermostatTwoToneIcon sx={{fontSize : "100px" , color : "white"}}/>{search}</h1>
                <p>{value.temp}°C</p>
                <h5>Min : {value.temp_min}°C | Max : {value.temp_max}°C</h5>
                <div className='wave -one'></div>        
                <div className='wave -two'></div>   
                <div className='wave -three'></div> 
            </div>

            
            
        </Paper>      

        </div>
    </>
  )

}
export default WeatherCard
